    <div style={{ padding: "20px" }}>
      <h2>Dashboard</h2>
      {user ? (
        <div>
          <p>
           üëãüèªWelcome, <strong> {user.name} </strong>!
          </p>
          <p> ‚úâÔ∏èEmail: {user.email} </p>
        </div>
      ) : (
        <p>{msg}</p>
      )}
    </div>
  );
};
export default Dashboard;
